var moveForce=35,rotateForce=25;$(document).mousemove(function(e){var o=$(document).width(),t=$(document).height(),c=(e.pageX-o/2)/(o/2)*-moveForce,a=(e.pageY-t/2)/(t/2)*-moveForce,r=e.pageX/o*rotateForce*2-rotateForce,s=-(e.pageY/t*rotateForce*2-rotateForce);$(".popup").css("left",c+"px").css("top",a+"px").css("transform","rotateX("+s+"deg) rotateY("+r+"deg)")}),$(document).keypress(function(e){switch(base=document.location.origin,e.charCode){case"b".charCodeAt():document.location.href=base+"/blog";break;case"c".charCodeAt():document.location.href=base+"/cs61a";break;case"p".charCodeAt():document.location.href=base+"/portfolio"}});